SQLPreSelect.Command:

            if exists ( select 1 from sysobjects where name='ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W' and xtype='U' )
                drop table ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W;

            select a.IDA,STUFF((select distinct ';' +  RTRIM(ar.IDROLEACTOR)   
                        from dbo.ACTOR a1 
                        inner join dbo.ACTORROLE ar on (ar.IDA=a.IDA) and (ar.IDROLEACTOR in (select IDROLEACTOR from dbo.ROLEACTOR))
                        where a1.IDA= a.IDA FOR XML PATH ('')),1,1,'') as ROLE  
						into ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W
            from dbo.ACTOR a
            where a.IDA = case when -1=%%DA:PK.GetDataSQLValue()%% then a.IDA else %%DA:PK.GetDataSQLValue()%% end 
						group by a.IDA;

            create index IX_ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W on ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W(IDA);


  declare
  lcount number;
  query varchar2(2000);  
  begin 
	  select count(1) into lcount from USER_TABLES where TABLE_NAME = 'ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W';
	  if (lcount > 0) then
	  	  query:='drop table ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W purge';
		    execute immediate query;
	  end if;

	  query:='create table ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W as	(
	  			 select a.IDA, RTRIM(REPLACE(REPLACE(XMLAgg(XMLElement("x", RTRIM(ar.IDROLEACTOR))		
					 order by ar.IDROLEACTOR ASC), ''<x>'', ''''), ''</x>'', '',''), '','') AS ROLE 
					 from dbo.ACTOR a	
					 inner join (select distinct IDA,IDROLEACTOR from dbo.ACTORROLE) ar on ar.IDA = a.IDA 
           where a.IDA = case when -1=%%DA:PK.GetDataSQLValue()%% then a.IDA else %%DA:PK.GetDataSQLValue()%% end 
           group by a.IDA)';
	  execute immediate query;

    query:='create index IX_ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W on ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W(IDA)';
    execute immediate query;
   end;


  declare
  lcount number;
  query varchar2(2000);  
  begin 
	  select count(1) into lcount from USER_TABLES where TABLE_NAME = 'ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W';
	  if (lcount > 0) then
	  	  query:='drop materialized view ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W';
		    execute immediate query;
	  end if;

	  query:='create materialized view ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W as	(
	  			 select a.IDA, RTRIM(REPLACE(REPLACE(XMLAgg(XMLElement("x", RTRIM(ar.IDROLEACTOR))		
					 order by ar.IDROLEACTOR ASC), ''<x>'', ''''), ''</x>'', '',''), '','') AS ROLE 
					 from dbo.ACTOR a	
					 inner join (select distinct IDA,IDROLEACTOR from dbo.ACTORROLE) ar on ar.IDA = a.IDA 
           where a.IDA = case when -1=%%DA:PK.GetDataSQLValue()%% then a.IDA else %%DA:PK.GetDataSQLValue()%% end 
           group by a.IDA)';
	  execute immediate query;

    query:='create index IX_ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W on ACTOR_IDROLE_%%SHORTSESSIONID%%.ToUpper()_W(IDA)';
    execute immediate query;
   end;
